<?php

declare(strict_types=1);

use Szhorvath\CloudflareStream\Concerns\StreamSigner;

it('should return a signed token', function () {
    $keyId = 'acfe424f5031ac99ddca47b7037e3446';
    $pem = '';

    $videoId = '143aaa0e8af3ed7d7f6cd173e5e01e6b';
    $expiresAt = new DateTimeImmutable('2024-12-01 00:00:00');
    $accessRules = [
        [
            'type' => 'ip.geoip.country',
            'action' => 'allow',
            'country' => ['GB'],
        ],
        [
            'type' => 'any',
            'action' => 'block',
        ],
    ];

    $streamSigner = new StreamSigner($pem, $keyId);

    $signedToken = $streamSigner->signedToken(
        videoId: $videoId,
        accessRules: $accessRules,
        expiresAt: $expiresAt,
        downloadable: true,
        availableAt: new DateTimeImmutable('2024-10-01 00:00:00'),
    );

    expect($signedToken)->toBe('eyJhbGciOiJSUzI1NiIsImtpZCI6ImFjZmU0MjRmNTAzMWFjOTlkZGNhNDdiNzAzN2UzNDQ2In0..AGLxUY7AtIG4V5Ss84yOyxkgnZYfFDOQmDzT2zmPZ5ep4r_NfPHhDF45jtuvowc5ybFDcuhhieJ5b3ixw-nbxkNQwHW4tSkWG3bTiiIVz2LuuhEI4NCZ82PFbb0oWPaaQnFJWgxOSGjQZZ3r8rT95-3JqbLpcTq4K11yYrHI0uZKVS8jzNv1X8sO0X1a6cxEg7roDpBTr2EH6uxSDH4DLBVRNDn3EN7FIYf4nI9azuiKEVVv8z74AT31QrhbfqbzpCNA3kDx5hc54Kcxi6Kn-4ojx76Cn1DGa6OsnFTGQWfhIc-ZctlXSM_kvpZ1aNXRmBl9hhAPSWrlyNgdx3nQOw');
});
